using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEngine.SceneManagement;

public class titleScript : MonoBehaviour
{
    float targetTime;
    bool cooldown;
    // Start is called before the first frame update
    void Start()
    {
        if((SceneManager.GetActiveScene()).name.Equals("Credits"))
        { 
            targetTime = 3.0f;
        }
        else if((SceneManager.GetActiveScene()).name.Equals("Title")|| (SceneManager.GetActiveScene()).name.Equals("VTitle"))
        {
            targetTime = 2.0f;
        }
        cooldown = true;
    }


    // Update is called once per frame
    void Update()
    {
        //Scene changer
        if(Input.GetAxis("Jump") == 1 && ((SceneManager.GetActiveScene()).name.Equals("Title") || (SceneManager.GetActiveScene()).name.Equals("VTitle")))
        {
            SceneManager.LoadScene("MainGameplay");
        }


        //Credits
        
        if ((Input.GetKey(KeyCode.J)) && (((SceneManager.GetActiveScene()).name.Equals("Title")) || (SceneManager.GetActiveScene()).name.Equals("VTitle")) && !cooldown)
        {
            Debug.Log("Report to credits immediately");
            SceneManager.LoadScene("Credits");
        }
        else if ((Input.GetKey(KeyCode.J) && (SceneManager.GetActiveScene()).name.Equals("Credits")) && !cooldown)
        {
            Debug.Log("RETREAT!");
            SceneManager.LoadScene("Title");
        }


        if (cooldown)
        {
            targetTime -= Time.deltaTime;

            if (targetTime <= 0.0f)
            {
                timerEnded();
            }


        }
    }



    void timerEnded()
    {
        cooldown = !true;
        Debug.Log("Cooldown: " +cooldown);
    }
}
